Analytics.Script:
CREATE TEMPORARY TABLE memberinfo
USING CarbonAnalytics
OPTIONS (tableName "MEMBER_INFO");

CREATE TEMPORARY TABLE memberdetails
USING CarbonAnalytics
OPTIONS (tableName "MEMBER_DETAILS",
schema "member_id STRING, cluster_instance_type STRING, scaling_decision_id STRING,is_multi_tenant STRING,private_IP_addresses STRING, public_IP_addresses STRING, allocated_IP_addresses STRING, host_name STRING, hypervisor STRING, cpu INT, ram INT, image_id STRING, login_port STRING, os_name STRING, os_version STRING, os_architecture STRING, is_64bit_os STRING");

INSERT OVERWRITE TABLE memberdetails select * from memberinfo;
